mapper.select:
SELECT  FROM TB_DEVICES

	and listnum like CONCAT('%',#{listnum},'%')
	and phone like CONCAT('%',#{phone},'%')
	and rktime >= #{rktime} 
	 and rktime <= #{rktime1} 
	and lytime >= #{lytime} 
	 and lytime <= #{lytime1} 
	and fhtime >= #{fhtime} 
	 and fhtime <= #{fhtime1} 
	and state =  #{state} 
	 and lyr like CONCAT('%',#{lyr},'%') 
	and firm like CONCAT('%',#{firm},'%')  
	and area like CONCAT('%',#{area},'%')  


	ORDER BY  ${sort}       ${order}


LIMIT #{startPage},#{endPage}

SELECT COUNT(1) FROM TB_DEVICES

	and listnum like CONCAT('%',#{listnum},'%')
	and phone like CONCAT('%',#{phone},'%')
	and rktime >= #{rktime} 
	 and rktime <= #{rktime1} 
	and lytime >= #{lytime} 
	 and lytime <= #{lytime1} 
	and fhtime >= #{fhtime} 
	 and fhtime <= #{fhtime1} 
	and state =  #{state} 
	 and lyr like CONCAT('%',#{lyr},'%') 
	and firm like CONCAT('%',#{firm},'%')  
	and area like CONCAT('%',#{area},'%')  


	ORDER BY  ${sort}  ${order}

mapper.update:
UPDATE TB_DEVICES SET 
	state=4 ,installers =#{installers},installtime=#{installtime},carNumber=#{carNumber},company=#{company},networkNo=#{networkNo}
WHERE ID=#{id}

UPDATE TB_DEVICES SET 
	test =#{test},phone=#{phone},tresult=#{tresult},cstime=now()
WHERE ID=#{id}

UPDATE TB_DEVICES SET 
	state=3 ,fhtime =now()
WHERE ID=#{id}

UPDATE TB_DEVICES SET 
	state=1 ,lyr =#{lyr},lytime=#{lytime},area=#{area}
WHERE ID=#{id}
